mapper.insert:
	INSERT INTO t_cart (
		uid, gid, num,
		created_user, created_time,
    	modified_user, modified_time
	) VALUES (
		#{uid}, #{gid}, #{num},
		#{createdUser}, #{createdTime},
    	#{modifiedUser}, #{modifiedTime}
	)

mapper.select:
SELECT cid, gid, t_cart.num, title, image, price FROM t_cart LEFT JOIN t_goods ON t_cart.gid=t_goods.id WHERE cid IN
#{cid}
ORDER BY t_cart.modified_time DESC
